import { MDBBtn, MDBInput,MDBInputGroup } from 'mdb-react-ui-kit';
const style = {
    width: "80%",
    marginTop:"20px",
    marginLeft:"15%",
}
const handleSubmitted =(e)=>{ 
    e.preventDefault();
    setInput("");
}

 const TodoForm = (props) => {
    const [input,setInput] = useState("");
        return ( 

        <form className="todo-form" >
            <MDBInputGroup style={style}>
             <MDBInput onChange={(e)=>setInput(e.target.value)} className="todo-list" label='Example label' placeholder="add a todo" id='form1'  />
             <MDBBtn type="submit" color='primary'>Add Todo</MDBBtn>
            </MDBInputGroup>
           
             
        </form>
        );
    };
    export default TodoForm;
