import React, { useState } from "react";
import { MDBBtn, MDBInput,MDBInputGroup } from 'mdb-react-ui-kit';
const style = {
    width: "80%",
    marginTop:"20px",
    marginLeft:"15%",
}


 const TodoForm = (props) => {
    const handleSubmitted =(e)=>{ 
        e.preventDefault();
        props.addTodo(input);
        setInput("");
    }
    const [input,setInput] = useState("");
        return ( 

        <form className="todo-form" style={style}>
            <MDBInputGroup >
             <MDBInput onChange={(e)=>setInput(e.target.value)} className="todo-list" label='Example label' placeholder="add a todo" id='form1'  />
             <MDBBtn onClick={()=>handleSubmitted} type="submit">Add Todo</MDBBtn>
            </MDBInputGroup>
           
             
        </form>
        );
    };
    export default TodoForm;
