import React,{useState} from 'react';
import { MDBBtn, MDBContainer } from 'mdb-react-ui-kit';
import './App.css';
import ExpenseItem from './components/ExpenseItem';
import ExpenseForm from './components/ExpenseForm';
import ExpenseList from './components/ExpenseList';
import Expense from './components/Expense'

import Alert from './components/Alert';
import { v4 as uuidv4 } from 'uuid';

const initialExpenses = [
  {id:uuidv4(),charge:"Rent",amount:1600},
  {id:uuidv4(),charge:"Car payment",amount:1800},
  {id:uuidv4(),charge:"Creadit card bill",amount:2000},

];



function App() {
  //*********state values */
  //****all expenses ad expenses */
  const [expenses,setExpense] = useState(initialExpenses);
  //single expense
  const [charge, setCharge] = useState('');
   //single amount
   const [amount, setAmount] = useState('');
  //****functionality */

  const handleCharge = e =>{

    setCharge(e.target.value)
  }
  const handleAmount = e =>{

    setAmount(e.target.value)
  }
  //alert 
  const[alert,setAlert] = useState({show:false});
  //handle alerts
  const handleAlert = ({type,text})=>{ 
    setAlert({show:true,type,text});
    setTimeout(()=>{
      setAlert({show:false})
    },3000)
  }

  //alert
  const handleSubmit = e =>{
    e.preventDefault()
    if (charge!== '' && amount>0) {
      const singleExpense = {id:uuidv4(),charge,amount};
      setExpense([...expenses,singleExpense]);
      setCharge("");
      setAmount("");

    }else{
      //handlealert
    }
  }

  return (
    <MDBContainer fluid>
      {alert.show && <Alert type={alert.type} text={alert.text}/>}

      <Alert/>
      <h1>Budget Calculator</h1>
      <main className="App">

      <ExpenseForm 
      charge={charge} 
      amount={amount} 
      handleAmount={handleAmount} 
      handleCharge={handleCharge}
      handleSubmit={handleSubmit}/>
      <ExpenseList expenses={expenses}/>


      </main>
      <h1>total spending:<span className="total">${expenses.reduce((acc,curr)=>{
        return (acc + parseInt(curr.amount));
      },0)}</span></h1>



    </MDBContainer>
  );
}

export default App;
